import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Sidebar from './Sidebar';

const Layout = ({ children }) => {
  const location = useLocation();
  const [heading, setHeading] = useState('');
  const [drawerOpen, setDrawerOpen] = useState({ right: false });

  const toggleDrawer = (anchor, open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) return;
    setDrawerOpen((prev) => ({ ...prev, [anchor]: open }));
  };

  useEffect(() => {
    const headings = {
      '/show-all-request': 'Request',
      '/show-request': 'Request',
      '/create-new-request': 'Request',
      '/edit-request': 'Request',
      '/show-all-review': 'Review',
      '/create-new-review': 'Review',
      '/edit-review': 'Review',
      '/show-review': 'Review',
      '/my-folder': 'Folder',
      '/my-module': 'Folder',
      '/my-file': 'Folder',
      '/my-profile': 'Profile',
      '/login': 'Login',
      '/archives': 'Archive',
      '/rollback-review': 'Archive',
      '/rollback-request': 'Archive',
      '/rollback-folder': 'Archive',
      '/rollback-subfolder': 'Archive',
      '/setting': 'Setting',
      '/change-password': 'Setting',
      '/feed-back': 'Feedback',
    };

    setHeading(headings[location.pathname] || 'Dashboard');
  }, [location]);

  return (
    <>
      <Sidebar />
      <Navbar heading={heading} toggleDrawer={toggleDrawer} />
      <div style={{ margintop: 0,  paddingtop: 0}}>
         {/* className="main_section me-3"> */}
        {children}
      </div>
    </>
  );
};

export default Layout;
