import React, { useState } from "react";

const OtpLogin = () => {
  const [mobile, setMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState("mobile");
  const fakeOtp = "123456";

  const sendOtp = () => {
    if (mobile.length === 10 && /^[0-9]+$/.test(mobile)) {
      alert(`OTP sent to ${mobile} (Use: ${fakeOtp})`);
      setStep("otp");
    } else {
      alert("Please enter a valid 10-digit mobile number");
    }
  };

  const verifyOtp = () => {
    if (otp === fakeOtp) {
      alert("✅ Login successful!");
    } else {
      alert("❌ Incorrect OTP. Try again.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.overlay}></div>
      <div style={styles.box}>
        <h2 style={styles.title}>Login with OTP</h2>

        {step === "mobile" && (
          <>
            <input
              type="text"
              placeholder="Enter Mobile Number"
              value={mobile}
              maxLength={10}
              onChange={(e) => setMobile(e.target.value)}
              style={styles.input}
            />
            <button onClick={sendOtp} style={styles.button}>
              Send OTP
            </button>
          </>
        )}

        {step === "otp" && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              style={styles.input}
            />
            <button onClick={verifyOtp} style={styles.button}>
              Verify OTP
            </button>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    backgroundImage: `url('https://images.unsplash.com/photo-1601597111158-3b1b0b78ef80?auto=format&fit=crop&w=1950&q=80')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    position: "relative",
  },
  overlay: {
    position: "absolute",
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0,0,0,0.4)",
    zIndex: 1,
  },
  box: {
    position: "relative",
    zIndex: 2,
    background: "rgba(255, 255, 255, 0.95)",
    padding: "40px 30px",
    borderRadius: "15px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.2)",
    width: "320px",
    textAlign: "center",
    backdropFilter: "blur(10px)",
  },
  title: {
    marginBottom: "25px",
    color: "#333",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  input: {
    width: "100%",
    padding: "12px",
    fontSize: "16px",
    margin: "12px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    outline: "none",
    transition: "all 0.3s ease-in-out",
  },
  button: {
    width: "100%",
    padding: "12px",
    backgroundColor: "#4CAF50",
    color: "white",
    fontSize: "16px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "10px",
    transition: "background-color 0.3s",
  },
};

export default OtpLogin;
