import React, { useState } from "react";

const OtpLogin = () => {
  const [mobile, setMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState("mobile"); // 'mobile' or 'otp'
  const fakeOtp = "123456"; // Simulated OTP

  const sendOtp = () => {
    if (mobile.length === 10 && /^[0-9]+$/.test(mobile)) {
      alert(`OTP sent to ${mobile} (Use: ${fakeOtp})`);
      setStep("otp");
    } else {
      alert("Please enter a valid 10-digit mobile number");
    }
  };

  const verifyOtp = () => {
    if (otp === fakeOtp) {
      alert("✅ Login successful!");
    } else {
      alert("❌ Incorrect OTP. Try again.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.box}>
        <h2 style={{ marginBottom: 20 }}>Login with OTP</h2>

        {step === "mobile" && (
          <>
            <input
              type="text"
              placeholder="Enter Mobile Number"
              value={mobile}
              maxLength={10}
              onChange={(e) => setMobile(e.target.value)}
              style={styles.input}
            />
            <button onClick={sendOtp} style={styles.button}>
              Send OTP
            </button>
          </>
        )}

        {step === "otp" && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              style={styles.input}
            />
            <button onClick={verifyOtp} style={styles.button}>
              Verify OTP
            </button>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    background: "#f0f2f5",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  box: {
    background: "#fff",
    padding: "30px",
    borderRadius: "10px",
    boxShadow: "0 0 15px rgba(0,0,0,0.1)",
    width: "300px",
    textAlign: "center",
  },
  input: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    margin: "10px 0",
    boxSizing: "border-box",
  },
  button: {
    width: "100%",
    padding: "10px",
    backgroundColor: "#4CAF50",
    color: "white",
    fontSize: "16px",
    border: "none",
    cursor: "pointer",
  },
};

export default OtpLogin;
