import React from 'react';
import Navbar from './Navbar';
import Slider from './Sidebar';

const Layout = ({ children }) => {
  return (
    <div style={{ minHeight: "100vh", display: "flex", flexDirection: "column" }}>
      {/* Fixed Navbar at the top */}
      <div style={{ position: "fixed", top: 0, left: 0, width: "100%", zIndex: 1030 }}>
        <Navbar />
      </div>

      <div style={{ display: "flex", flex: 1, marginTop: "56px" }}>
        {/* Sidebar (fixed height) */}
        <div
          style={{
            width: "250px",
            height: "calc(100vh - 56px)",
            position: "fixed",
            top: "56px",
            left: 0,
            overflowY: "auto",
            background: "#343a40", // dark theme example
            color: "#fff",
          }}
        >
          <Slider />
        </div>

        {/* Main Content */}
        <div
          style={{
            marginLeft: "250px",
            padding: "20px",
            flexGrow: 1,
            backgroundColor: "#f8f9fa", // light background for content
            minHeight: "calc(100vh - 56px)",
          }}
        >
          {children}
        </div>
      </div>
    </div>
  );
};

export default Layout;
